'use strict';
(function myMain() {			// this will auto-run when script is executed

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
PURPOSE:	Get Date Difference (in days)
VER:			2.0		2019-02-18
AUTHOR:		@JMichaelTX
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/

var app = Application.currentApplication()
app.includeStandardAdditions = true

var kmInst = app.systemAttribute("KMINSTANCE");
var kmeApp = Application("Keyboard Maestro Engine")

//--- Get Date String from KM ---
var dateSource = kmeApp.getvariable("Local__Date",  {instance: kmInst});
//==>'Fri, Mar 01'

//--- Get First Capture Group ---
var monthDayStr = dateSource.match(/^\w+, (.+)/)[1];
//==>"Mar 01"

var daysDiff = LPad(getDateDiff(monthDayStr),'0',2);

return daysDiff;

 function getDateDiff(pMonthDay){

		var yearStr = (new Date()).getFullYear().toString();
		//--- Append Current Year to Date String ---
    var dateUnix = Date.parse(pMonthDay + ", " + yearStr);
		var dt2 = new Date(dateUnix);
		var dt1 = new Date()	// today

		var dateDiff = Math.floor((Date.UTC(dt2.getFullYear(), dt2.getMonth(), dt2.getDate()) - Date.UTC(dt1.getFullYear(), dt1.getMonth(), dt1.getDate()) ) /(1000 * 60 * 60 * 24));
		return dateDiff;
 }

function LPad(pNum, pPadChar, pLength) {
	var signNum = (pNum < 0) ? '-' : '';
	var numStr = Math.abs(pNum).toString();
	while (numStr.length < pLength) {
		numStr = pPadChar + numStr;
	}

	return signNum + numStr;

}

}	// END of function myMain()
)();	// autorun
