(function(inDesignMode, isPlugin, designModeParams) {
	'use strict';
	if (isPlugin) {
		ObjC.import('stdlib');
	}
	var _kme = Application("Keyboard Maestro Engine");

	// =============== Prototypes ============================================
	String.prototype.escapeRegExp = function() {
		return this.replace(/([.*+?^=!:${}()|\[\]\/\\])/g, "\\$1");
	};

	if (!String.prototype.endsWith) {
		String.prototype.endsWith = function(searchString, position) {
			var subjectString = this.toString();
			if (typeof position !== 'number' || !isFinite(position) || Math.floor(position) !== position || position > subjectString.length) {
				position = subjectString.length;
			}
			position -= searchString.length;
			var lastIndex = subjectString.indexOf(searchString, position);
			return lastIndex !== -1 && lastIndex === position;
		};
	}

	// =============== getParam ==============================================
	function getParam(name, required) {
		function getDesignModeParam() {
			var result  = designModeParams[name];
			if (result === undefined)
				throw Error("DesignModeParam '" + name + "' is not defined");
			return result;
		}

		function getPluginParam() {
			var envParamName = "KMPARAM_" + name;
			result = $.getenv(envParamName);
			return result ? result.trim() : "";
		}

		function getMacroParam() {
			var envParamName = "orut_" + name;
			if (!_kme)
				_kme = Application("Keyboard Maestro Engine");
			return _kme.getvariable(envParamName)
		}

		// getParam()
		var result = inDesignMode && designModeParams ? getDesignModeParam() :
			isPlugin ? getPluginParam() : getMacroParam();
		if (required && !result)
			throw Error("Variable '" + name.replace(/_/g, " ") + "' is required");
		return result;
	} // getParam()

	// =============== makeBrowserObject =====================================
	function makeBrowserObject(browserName) {
		return (function(browserName) {
			var _browserName = browserName === "Chrome" ? "Google Chrome" : browserName;
			var _app = Application(_browserName);

			function _escapeXml(str) {
			    return str.replace(/[<>&'"]/g, function (c) {
			        switch (c) {
			            case '<': return '&lt;';
			            case '>': return '&gt;';
			            case '&': return '&amp;';
			            case '\'': return '&apos;';
			            case '"': return '&quot;';
			        }
			    });
			}

			var result = {
				// Common functions
				activate: function() {
					_app.activate();
				},

				createNewTab: function(url) {
					var tabInfo = {};
					tabInfo.tab = _app.Tab({url: url});
					tabInfo.window = this.getWindows(true)[0];
					tabInfo.tabIndex = tabInfo.window.tabs.push(tabInfo.tab);
					this.setActiveTab(tabInfo);
				},

				findTabWithURL: function(target) {
					var result = null;
					var emptyTabResult = null;
					var windows = this.getWindows(true);
					for (var i = 0; i < windows.length; i++) {
						var window = windows[i];
						var tabs = window.tabs();
						for (var j = 0; j < tabs.length; j++) {
							var tab = tabs[j];
							if (target.matchesURL(tab.url())) {
								var isActiveTab = this.isActiveTab(window, tab);
								if (!result || isActiveTab) {
									result = {
										window: window,
										tab: tab,
										tabIndex: j + 1,
										isEmptyTab: false
									};
									if (isActiveTab)
										return result;
								}
							} else if (!emptyTabResult && this.tabIsEmpty(tab)) {
								emptyTabResult = {
									window: window,
									tab: tab,
									tabIndex: j + 1,
									isEmptyTab: true
								};
							}
						}
					}
					return result || emptyTabResult;
				},

				reload: function(tabInfo) {
					tabInfo.tab.url = tabInfo.tab.url();
				},

				setActiveWindow: function(window) {
					var script =
						'<dict> \n' +
						'	<key>Action</key> \n' +
						'	<string>SelectWindow</string> \n' +
						'	<key>HeightExpression</key> \n' +
						'	<string>300</string> \n' +
						'	<key>HorizontalExpression</key> \n' +
						'	<string>125</string> \n' +
						'	<key>MacroActionType</key> \n' +
						'	<string>ManipulateWindow</string> \n' +
						'	<key>NotifyOnFailure</key> \n' +
						'	<false/> \n' +
						'	<key>Targeting</key> \n' +
						'	<string>NamedWindow</string> \n' +
						'	<key>TargetingType</key> \n' +
						'	<string>Front</string> \n' +
						'	<key>VerticalExpression</key> \n' +
						'	<string>125</string> \n' +
						'	<key>WidthExpression</key> \n' +
						'	<string>300</string> \n' +
						'	<key>WindowIndexExpression</key> \n' +
						'	<string>2</string> \n' +
						'	<key>WindowName</key> \n' +
						'	<string>##windowName##</string> \n' +
						'</dict>';
					script = script.replace("##windowName##", _escapeXml(window.name()));
					delay(0.1);
					_kme.doScript(script);
				}
			};

			// Browser-specific functions
			switch (_browserName) {
				case "Safari":
					result.createNewWindow = function() {
						_app.Document().make();
					};

					result.getWindows = function(createIfNone) {
						var result = _app.windows.whose({_not: [{document: null}]});
						if (result.length > 0 || !createIfNone)
							return result;
						this.createNewWindow();
						return _app.windows.whose({_not: [{document: null}]});
					};

					result.isActiveTab = function(window, tab) {
						return window.currentTab().index() === tab.index();
					};

					result.setActiveTab = function(tabInfo) {
						tabInfo.window.currentTab = tabInfo.tab;
						this.setActiveWindow(tabInfo.window);
					};

					result.tabIsEmpty = function(tab) {
						return !tab.url();
					};
					break;
				case "Google Chrome":
					result.createNewWindow = function() {
						_app.Window().make();
					};

					result.getWindows = function(createIfNone) {
						var result = _app.windows();
						if (result.length > 0 || !createIfNone)
							return result;
						this.createNewWindow();
						return _app.windows();
					};

					result.isActiveTab = function(window, tab) {
						return window.activeTab().id() === tab.id();
					};

					result.setActiveTab = function(tabInfo) {
						tabInfo.window.activeTabIndex = tabInfo.tabIndex;
						this.setActiveWindow(tabInfo.window);
					};

					result.tabIsEmpty = function(tab) {
						return !tab.url() || tab.url() === "chrome://newtab/";
					};
					break;
				default:
					throw Error("Unsupported browser name '" + _browserName + "'");
			}

			return result;
		})(browserName);
	}

	// =============== makeTargetObject ======================================
	function makeTargetObject(url, matchStart) {
		return (function(url, matchStart) {
			var _url = url;
			var _pattern;

			if (_url.endsWith("*")) {
				matchStart = true;
				_url = _url.substring(0, _url.length - 1);
			}

			var urlPattern = _url.escapeRegExp().replace(/^https?/i, "https?");
			if (matchStart) {
				_pattern = new RegExp("^" + urlPattern, "i");
			} else {
				_pattern = new RegExp("^" + urlPattern + "$", "i");
			}
			return {
				matchesURL: function(url) {
					return url && _pattern.test(url);
					// return url && (new RegExp(_pattern, "i")).test(url);
				},

				getURL: function() {
					return _url;
				}
			};
		})(url, matchStart);
	}

	// =============== execute ================================================
	function execute() {
		var browser = makeBrowserObject(getParam("Browser", true));
		var target = makeTargetObject(getParam("URL", true), getParam("Match_Start") === "1");
		var reloadExisting = getParam("Reload_Existing_Tab") === "1";

		browser.activate();
		var tabInfo = browser.findTabWithURL(target);
		if (tabInfo) {
			if (tabInfo.isEmptyTab)
				tabInfo.tab.url = target.getURL();
			browser.setActiveTab(tabInfo);
			if (reloadExisting && !tabInfo.isEmptyTab)
				browser.reload(tabInfo);
		} else {
			browser.createNewTab(target.getURL());
		}

		return isPlugin ? "" : "OK";
	}

	if (inDesignMode) {
        return execute();
    } else {
        try {
            return execute();
        } catch (e) {
            return "Error: " + e.message;
        }
    }
})(false, false);
