				var error;
				var contents =
					ObjC.deepUnwrap(
						$.NSFileManager.defaultManager[method](
							$(strPath)
							.stringByStandardizingPath, error
						)
					);
				if (error)
					throw Error('Could not get contents of "' + strPath + '"');
				if (!contents)
					contents = [];
				if (!includeDotFiles) {
					contents = contents.filter(function(item) {
						return !item.startsWith(".") && !item.includes("/.");
