				if (!macro.Actions || macro.Actions.length === 0)
					return null;

				var action = macro.Actions[0];
				if (action.MacroActionType !== "Comment")
					return null;

				return {
					name: action.ActionName || action.Title || "",
					title: action.Title || "",
					text: action.Text || ""
				};
			},

			getKMActionsXmlFromClipboard(throwIfNotFound) {
