///使用方法
func DCPostCommandAndKey(keyCode:CGKeyCode){

        let src = CGEventSourceCreate(CGEventSourceStateID.HIDSystemState)

        let keyDown = CGEventCreateKeyboardEvent(src, keyCode, true)
        let keyUp = CGEventCreateKeyboardEvent(src, keyCode, false)
        let loc = CGEventTapLocation.CGHIDEventTap
        CGEventPost(loc, keyDown)
        CGEventPost(loc, keyUp)
}
