(function(inDesignMode, designModeParams) {
	'use strict';

	var _kme = Application("Keyboard Maestro Engine");

	function getKMVariable(name, required) {
		var result;
		if (inDesignMode && designModeParams) {
			result = designModeParams[name];
			if (result === undefined)
			throw Error("DesignModeParam '" + name + "' is not defined");
		} else {
			result = _kme.getvariable(name);
		}
		if (required && !result)
			throw Error("Variable '" + name + "' is required");
		return result;
	}

	function wordWrap(str, width, cut) {
		if (!str || width <= 0) {
			return [str];
		}
		cut = cut || false;

		var regex = new RegExp('.{1,' + width + '}(\\s|$)' + (cut ? '|.{' + width + '}|.+$' : '|\\S+?(\\s|$)'), 'g');
		var matches = str.match(regex);
		if (!matches)
			return str;
		return matches.join("\n");
	}

	function execute() {
		return wordWrap(getKMVariable("wordWrap_text"), parseInt(getKMVariable("wordWrap_width")));
	}

	if (inDesignMode) {
        return execute();
    } else {
        try {
            return execute();
        } catch (e) {
            return "Error: " + e.message;
        }
    }
})(false);
